mapper.insert:
INSERT INTO approval_file(ap_file_no, d_no, ap_filename, ap_fileorigin, ap_filepath, reg_date, mod_date, reg_id, mod_id) SELECT seq_approval_file_no.nextval, A.* from (
SELECT #{item.d_no} as d_no, #{item.ap_filename} as ap_filename, #{item.ap_fileorigin} as ap_fileorigin, #{item.ap_filepath} as ap_filepath, sysdate as reg_date, sysdate as mod_date, #{item.e_no} as reg_id, #{item.e_no} as mod_id from dual
) A

insert into APPROVAL_HISTORY(ap_no, ap_lev, ap_result, ap_id, ap_cdate, ap_category, d_no, ap_comment, reg_date, mod_date, reg_id, mod_id) select SEQ_APPROVAL_HISTORY_NO.nextval, A.* from (
select #{item.ap_lev, jdbcType=NUMERIC} as ap_lev, '대기중' as ap_result, #{item.ap_id} as ap_id, null as ap_cdate, #{item.ap_category} as ap_category, #{item.d_no, jdbcType=NUMERIC} as d_no, null as ap_comment, sysdate as reg_date, sysdate as mod_date, #{item.ap_id} as reg_id, #{item.ap_id} as mod_id from dual
) A

mapper.update:
update approval_doc
set
    d_status=#{d_status}
where d_no =#{d_no}
