mapper.insert:
INSERT INTO message_file(me_file_no, me_no, me_filename, me_fileorigin, me_filepath, reg_date, mod_date, reg_id, mod_id) SELECT seq_message_no.nextval, A.* from (
SELECT #{item.me_no} as me_no, #{item.me_filename} as me_filename, #{item.me_fileorigin} as me_fileorigin, #{item.me_filepath} as me_filepath, sysdate as reg_date, sysdate as mod_date, #{item.send_id} as reg_id, #{item.send_id} as mod_id from dual
) A

mapper.update:
update MESSAGE_RECIPIENT
set read_status = 'Y'
where r_id = #{r_id} and me_no = #{me_no}

update MESSAGE_RECIPIENT
set me_status = #{me_status}
where r_id = #{r_id} and me_no = #{me_no}
