mapper.insert:
INSERT INTO notice_file(n_file_no, n_no, n_filename, n_fileorigin, n_filepath, reg_date, mod_date, reg_id, mod_id) SELECT seq_noticefile_no.nextval, A.* from (
SELECT #{item.n_no} as n_no, #{item.n_filename} as n_filename, #{item.n_fileorigin} as n_fileorigin, #{item.n_filepath} as n_filepath, sysdate as reg_date, sysdate as mod_date, #{item.e_no} as reg_id, #{item.e_no} as mod_id from dual
) A
