mapper.update:
UPDATE PROGRAM_SCHEDULE
PS_NAME = #{PS_NAME}, PS_START = TO_DATE(#{PS_START}, 'YYYY-MM-DD"T"HH24:MI:SS'), PS_END = TO_DATE(#{PS_END}, 'YYYY-MM-DD"T"HH24:MI:SS'), PS_INFO = #{PS_INFO}, MOD_ID = #{MOD_ID}, REG_ID = #{REG_ID}, MOD_DATE = TO_DATE(sysdate, 'YYYY-MM-DD"T"HH24:MI:SS'), REG_DATE = TO_DATE(#{REG_DATE}, 'YYYY-MM-DD"T"HH24:MI:SS'), PS_DAYSOFWEEK = #{PS_DAYSOFWEEK}, PS_CATEGORY = #{PS_CATEGORY}
WHERE PS_NO = #{PS_NO}

mapper.delete:
DELETE FROM PROGRAM_SCHEDULE
WHERE PG_NO = #{pg_no}
    AND PS_START >= (SELECT MOD_DATE FROM PROGRAM WHERE PG_NO = #{pg_no})
